import React from 'react';

const Education = () => {
  const education = [
    {
      degree: 'Master of Science in Data Science',
      school: 'Indiana University Bloomington',
      period: 'August 2023 - May 2025',
      relevant_coursework: ['Statistics', 'Applied Algorithms', 'Database Concepts', 'Machine Learning in Econ', 'Data Mining', 'Big Data', 'Natural Language Processing', 'Data Visualization', 'Cloud Computing'],
    },
    {
      degree: 'Bachelor of Engineering in Computer Science and Engineering',
      school: 'St.Joseph Engineering College',
      period: 'May 2019 - April 2023',
      relevant_coursework: 'Statistics, Applied Algorithms, Database Concepts, Machine Learning in Econ, Data Mining, Big Data, Natural Language Processing, Data Visualization, Cloud Computing',
    },
  ];

  return (
    <div className="min-h-screen flex flex-col justify-center items-center px-4 max-w-4xl mx-auto">
      <h2 className="text-4xl font-bold text-white mb-8">Education</h2>
      <div className="w-full space-y-8">
        {education.map((edu, index) => (
          <div
            key={index}
            className="bg-gray-800 p-6 rounded-lg transform transition-transform duration-300 hover:scale-105"
          >
            <h3 className="text-xl font-semibold text-white mb-2">{edu.degree}</h3>
            <p className="text-blue-400 mb-2">{edu.school}</p>
            <p className="text-gray-400 mb-4">{edu.period}</p>
            {Array.isArray(edu.relevant_coursework) ? (
              <div>
                <h5 className="text-lg font-medium text-white mb-2">Relevant Coursework</h5>
                <ul className="list-disc pl-4 text-gray-300">
                  {edu.relevant_coursework.map((item, i) => (
                    <li key={i} className="mb-2">{item}</li>
                  ))}
                </ul>
              </div>
            ) : (
              <p className="text-gray-300">{edu.relevant_coursework}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Education;
