import React from 'react';

const Skills = () => {
  const skillCategories = [
    {
      category: 'Web Development',
      skills: ['MERN Stack', 'RESTful APIs', 'Flask', 'Django']
    },

    {
      category: 'Database',
      skills: ['MongoDB', 'PostgreSQL', 'MySQL', 'Redis', 'Firebase'],
    },
    {
      category: 'Data Science & ML',
      skills: ['Python', 'TensorFlow', 'PyTorch', 'Scikit-learn', 'Pandas', 'NumPy'],
    },
    {
      category: 'DevOps & Tools',
      skills: ['Git', 'Docker', 'AWS', 'CI/CD', 'Linux', 'Kubernetes'],
    },
    {
      category: 'Soft Skills',
      skills: ['Problem Solving', 'Team Leadership', 'Communication', 'Project Management', 'Agile/Scrum'],
    },
  ];

  return (
    <div className="min-h-screen flex flex-col justify-center items-center px-4">
      <h2 className="text-4xl font-bold text-white mb-8">Skills</h2>
      <div className="max-w-6xl w-full grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        {skillCategories.map((category, index) => (
          <div
            key={index}
            className="bg-gray-800 p-6 rounded-lg transform transition-transform duration-300 hover:scale-105"
          >
            <h3 className="text-xl font-semibold text-white mb-4">{category.category}</h3>
            <div className="flex flex-wrap gap-2">
              {category.skills.map((skill, skillIndex) => (
                <span
                  key={skillIndex}
                  className="px-3 py-1 bg-blue-500/20 text-blue-400 rounded-full text-sm"
                >
                  {skill}
                </span>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Skills;
