import React, { useState, useMemo } from 'react';

const Projects = () => {
  const [selectedTech, setSelectedTech] = useState('all');

  const projects = [
    {
      title: 'E-Commerce Platform',
      description: 'A full-stack e-commerce platform built with React, Node.js, and MongoDB. Features include user authentication, product management, and payment integration.',
      technologies: ['React', 'Node.js', 'MongoDB', 'Express', 'Stripe'],
      image: 'project1.jpg',
      github: '#',
      demo: '#',
    },
    {
      title: 'Machine Learning Dashboard',
      description: 'Interactive dashboard for visualizing machine learning model performance and predictions. Built with Python, TensorFlow, and React.',
      technologies: ['Python', 'TensorFlow', 'React', 'D3.js', 'Flask'],
      image: 'project2.jpg',
      github: '#',
      demo: '#',
    },
    {
      title: 'Task Management App',
      description: 'A collaborative task management application with real-time updates and team features. Built using React and Firebase.',
      technologies: ['React', 'Firebase', 'Material-UI', 'Redux'],
      image: 'project3.jpg',
      github: '#',
      demo: '#',
    },
  ];

  // Get unique technologies from all projects
  const allTechnologies = useMemo(() => {
    const techSet = new Set();
    projects.forEach(project => {
      project.technologies.forEach(tech => techSet.add(tech));
    });
    return ['all', ...Array.from(techSet)].sort();
  }, [projects]);

  // Filter projects based on selected technology
  const filteredProjects = useMemo(() => {
    if (selectedTech === 'all') return projects;
    return projects.filter(project => 
      project.technologies.includes(selectedTech)
    );
  }, [projects, selectedTech]);

  return (
    <div className="min-h-screen flex flex-col justify-center items-center px-4">
      <h2 className="text-4xl font-bold text-white mb-8">Projects</h2>

      {/* Filter Buttons */}
      <div className="flex flex-wrap justify-center gap-2 mb-8">
        {allTechnologies.map((tech) => (
          <button
            key={tech}
            onClick={() => setSelectedTech(tech)}
            className={`px-4 py-2 rounded-lg transition-colors duration-300 ${
              selectedTech === tech
                ? 'bg-blue-500 text-white'
                : 'bg-gray-800 text-gray-300 hover:bg-gray-700'
            }`}
          >
            {tech.charAt(0).toUpperCase() + tech.slice(1)}
          </button>
        ))}
      </div>

      {/* Projects Grid */}
      <div className="max-w-6xl w-full grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        {filteredProjects.map((project, index) => (
          <div
            key={index}
            className="bg-gray-800 rounded-lg overflow-hidden transform transition-transform duration-300 hover:scale-105"
          >
            <div className="h-48 bg-gray-700 flex items-center justify-center">
              {/* Add project image here */}
              <span className="text-gray-400">Project Image</span>
            </div>
            <div className="p-6">
              <h3 className="text-xl font-semibold text-white mb-2">{project.title}</h3>
              <p className="text-gray-300 mb-4">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-4">
                {project.technologies.map((tech, techIndex) => (
                  <span
                    key={techIndex}
                    className="px-3 py-1 bg-blue-500/20 text-blue-400 rounded-full text-sm"
                  >
                    {tech}
                  </span>
                ))}
              </div>
              <div className="flex gap-4">
                <a
                  href={project.github}
                  className="text-gray-300 hover:text-blue-400 transition-colors duration-300"
                >
                  GitHub
                </a>
                <a
                  href={project.demo}
                  className="text-gray-300 hover:text-blue-400 transition-colors duration-300"
                >
                  Live Demo
                </a>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* No Projects Message */}
      {filteredProjects.length === 0 && (
        <div className="text-center text-gray-400 mt-8">
          No projects found with the selected technology.
        </div>
      )}
    </div>
  );
};

export default Projects;
