import React from 'react';

const Education = () => {
  const education = [
    {
      degree: 'Master of Science in Data Science',
      school: 'Indiana University Bloomington',
      period: 'August 2023 - May 2025',
      relevant_coursework: 'Relevant Coursework:\endline Statistics, Applied Algorithms, Database Concepts, Machine Learning in Econ, Data Mining, Big Data, Natural Language Processing, Data Visualization, Cloud Computing',
    },
    {
      degree: 'Bachelor of Science in Computer Science',
      school: 'University Name',
      period: '2016 - 2020',
      description: 'Statistics, Applied Algorithms, Database Concepts, Machine Learning in Econ, Data Mining, Big Data, Natural Language Processing, Data Visualization, Cloud Computing',
    },
  ];

  return (
    <div className="min-h-screen flex flex-col justify-center items-center px-4 max-w-4xl mx-auto">
      <h2 className="text-4xl font-bold text-white mb-8">Education</h2>
      <div className="w-full space-y-8">
        {education.map((edu, index) => (
          <div
            key={index}
            className="bg-gray-800 p-6 rounded-lg transform transition-transform duration-300 hover:scale-105"
          >
            <h3 className="text-xl font-semibold text-white mb-2">{edu.degree}</h3>
            <p className="text-blue-400 mb-2">{edu.school}</p>
            <p className="text-gray-400 mb-4">{edu.period}</p>
            <p className="text-gray-300">{edu.relevant_coursework}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Education;
